import './App.css';
import MenuItem from './components/MenuItem';
import JapaneseRestaurant from './components/JapaneseRestaurant';
import React, { useState } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css'; // This imports bootstrap css styles. You can use bootstrap or your own classes by using the className attribute in your elements.

// Menu data. An array of objects where each object represents a menu item. Each menu item has an id, title, description, image name, and price.
// You can use the image name to get the image from the images folder.
const menuItems = [
  {
    id: 1,
    title: 'Gyoza',
    description: 'Japanese dumplings',
    imageName: 'gyoza.png',
    price: 5.99,
  },
  {
    id: 2,
    title: 'Sushi',
    description: 'Japanese rice rolls',
    imageName: 'sushi.png',
    price: 6.99,
  },
  {
    id: 3,
    title: 'Ramen',
    description: 'Japanese noodle soup',
    imageName: 'ramen.png',
    price: 7.99,
  },
  {
    id: 4,
    title: 'Matcha Cake',
    description: 'Japanese green tea cake',
    imageName: 'matcha-cake.png',
    price: 4.99,
  },
  {
    id: 5,
    title: 'Mochi',
    description: 'Japanese rice cake',
    imageName: 'mochi.png',
    price: 3.99,
  },
  {
    id: 6,
    title: 'Yakitori',
    description: 'Japanese skewered chicken',
    imageName: 'yakitori.png',
    price: 2.99,
  },
  {
    id: 7,
    title: 'Takoyaki',
    description: 'Japanese octopus balls',
    imageName: 'takoyaki.png',
    price: 5.99,
  },
  {
    id: 8,
    title: 'Sashimi',
    description: 'Japanese raw fish',
    imageName: 'sashimi.png',
    price: 8.99,
  },
  {
    id: 9,
    title: 'Okonomiyaki',
    description: 'Japanese savory pancake',
    imageName: 'okonomiyaki.png',
    price: 6.99,
  },
  {
    id: 10,
    title: 'Katsu Curry',
    description: 'Japanese curry with fried pork',
    imageName: 'katsu-curry.png',
    price: 9.99,
  }
];


function App() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  // Function to add an item to the cart
  const addItem = (item) => {
    const existingItem = cart.find(cartItem => cartItem.id === item.id);
    if (existingItem) {
      setCart(cart.map(cartItem => cartItem.id === item.id ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem));
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
    setTotal(prevTotal => prevTotal + item.price);
  };

  // Function to remove an item from the cart
  const removeItem = (itemId) => {
    const existingItem = cart.find(cartItem => cartItem.id === itemId);
    if (existingItem.quantity > 1) {
      setCart(cart.map(cartItem => cartItem.id === itemId ? { ...cartItem, quantity: cartItem.quantity - 1 } : cartItem));
      setTotal(prevTotal => prevTotal - existingItem.price);
    } else {
      setCart(cart.filter(cartItem => cartItem.id !== itemId));
      setTotal(prevTotal => prevTotal - existingItem.price);
    }
  };

  // Function to clear all items from the cart
  const clearCart = () => {
    setCart([]);
    setTotal(0);
  };

  // Function to handle the order button click
  const handleOrder = () => {
    // Logic to display order summary/receipt
    // This could be a modal or a new component showing the cart contents
    console.log("Order placed:", cart, "Total:", total);
  };
  return (
    <div>
      <JapaneseRestaurant />
      <div className="menu">
      Â {menuItems.map(item => 
      (<MenuItem 
          id = {item.id} 
          title = {item.title} 
          description = {item.description}
          imageName = {item.imageName}
          price = {item.price}/>))}

      </div>
    </div>
  );
}

export default App;
